import { Component, OnInit } from '@angular/core';
import { NavigationEnd, Router } from '@angular/router';
import { filter } from 'rxjs';
import { AccountService } from './services/account.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
})
export class AppComponent implements OnInit {
  title = 'HeadHunter';
  isExpired: boolean = false;

  constructor(private router: Router, private accountService: AccountService) {}

  ngOnInit(): void {
    this.router.events
      .pipe(filter((event) => event instanceof NavigationEnd))
      .subscribe(() => {
        if (this.accountService.isTokenExpired()) this.isExpired = true;
      });
  }
}
<app-nav-bar></app-nav-bar>

<div id="content_wrapper">
  <router-outlet></router-outlet>
</div>

<div class="overlay" *ngIf="isExpired">
  <div class="modal">
    <h2>Срок действия токена истёк</h2>
    <p>
      Пожалуйста, войдите в систему снова, чтобы продолжить использование
      приложения.
    </p>
    <button>Войти снова</button>
  </div>
</div>

