import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { VacancyDetailsService } from './vacancy-details.service';
import { ICompany, IVacancy } from '../interfaces/vacancy';

@Component({
  selector: 'app-vacancy-details',
  templateUrl: './vacancy-details.component.html',
  styleUrls: ['./vacancy-details.component.scss'],
})
export class VacancyDetailsComponent implements OnInit {
  vacancy?: IVacancy;
  company?: ICompany;

  constructor(
    private activatedRoute: ActivatedRoute,
    private detailsService: VacancyDetailsService
  ) {}

  ngOnInit(): void {
    this.getVacancy();
    this.getCompany();
  }

  getVacancy() {
    const id = this.activatedRoute.snapshot.paramMap.get('id');
    if (id) {
      this.detailsService.getVacancy(Number(id)).subscribe({
        next: (vac) => {
          this.vacancy = vac;
          this.detailsService.companyId = vac.companyId;
        },
      });
    }
  }

  getCompany() {
    if (this.detailsService.companyId !== 0) {
      this.detailsService.getCompany().subscribe({
        next: (data) => (this.company = data),
      });
    }
  }
}
<div class="mil-container">
  <div class="card">
    <div class="card-content">
      <div class="card-title">{{ vacancy?.position }}</div>
      <div class="card-subtitle">{{ vacancy?.salary }} $</div>
      <div class="card-company">
        {{ vacancy?.companyName }}
      </div>
      <div class="card-location" style="margin-bottom: 10px">
        {{ vacancy?.location }}
      </div>
      <a class="card-button">Откликнуться</a>
    </div>
  </div>
  <div class="card">
    <div class="card-content">
      <div class="card-title">{{ company?.name }}</div>
      <div class="card-subtitle">{{ company?.industry }} $</div>
      <div class="card-company">
        {{ company?.foundedYear }}
      </div>
      <div class="card-location" style="margin-bottom: 10px">
        {{ company?.vacancies?.length }}
      </div>
    </div>
  </div>
</div>
