import { Component, OnInit } from '@angular/core';
import { ShopParams } from '../interfaces/shopParams';
import { CatalogService } from './catalog.service';
import { IVacancy } from '../interfaces/vacancy';

@Component({
  selector: 'app-catalog',
  templateUrl: './catalog.component.html',
  styleUrls: ['./catalog.component.scss'],
})
export class CatalogComponent implements OnInit {
  vacancies: IVacancy[] = [];
  shopParams = new ShopParams();

  constructor(private catalogService: CatalogService) {}

  ngOnInit() {
    this.getVacancies();
  }

  getVacancies() {
    this.catalogService.getVacancies(this.shopParams).subscribe({
      next: (response) => {
        console.log(response);
        this.vacancies = response.data;
        this.shopParams.pageNumber = response.pageIndex;
        this.shopParams.pageSize = response.pageSize;
      },
    });
  }
}
<div style="display: flex; flex-wrap: wrap; justify-content: center">
  <div *ngFor="let item of vacancies">
    <div class="card">
      <div class="card-content">
        <div class="card-title">{{ item.position }}</div>
        <div class="card-subtitle">{{ item.salary }} $</div>
        <div class="card-company" style="color: rgb(90, 93, 95)">
          {{ item.companyName }}
        </div>
        <div class="card-location">{{ item.location }}</div>
        <a href="#" class="card-button">Узнать Больше</a>
      </div>
    </div>
  </div>
</div>
