import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-vacancy-list',
  templateUrl: './vacancy-list.component.html',
  styleUrls: ['./vacancy-list.component.scss'],
})
export class VacancyListComponent implements OnInit {
  search: string = '';

  constructor(private router: Router, private activatedRoute: ActivatedRoute) {}

  ngOnInit(): void {
    this.activatedRoute.queryParams.subscribe((params) => {
      console.log(params);
      this.search = params['query'] || '';
    });
  }

  // onRemove() {
  //   console.log(this.activatedRoute.snapshot.paramMap.has('query'));
  //   return this.activatedRoute.snapshot.paramMap.has('query');
  // }

  onSearch() {
    if (this.search) {
      this.router.navigate(['catalog'], {
        queryParams: { query: this.search },
      });
    }
  }
}
<div class="mil-container">
  <div class="search-container mb-3">
    <input
      type="text"
      class="search-input"
      id="search"
      name="search"
      placeholder="Найти вакансии..."
      [value]="search"
      [(ngModel)]="search"
    />
    <button class="search-button" (click)="onSearch()">Найти</button>
    <!-- <button class="search-button" *ngIf="onRemove()">Сбросить</button> -->
  </div>
  <app-catalog></app-catalog>
</div>
