import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';
import { MainService } from './main.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-main',
  templateUrl: './main.component.html',
  styleUrls: ['./main.component.scss'],
})
export class MainComponent implements OnInit {
  search: string = '';
  vacancyCount?: number;
  resumeCount?: number;
  companyCount?: number;

  constructor(private main: MainService, private router: Router) {}

  ngOnInit(): void {
    this.getVacancyCount();
    this.getResumeCount();
    this.getCompanyCount();
  }

  getVacancyCount() {
    this.main.getVacancyCount().subscribe((data) => {
      this.vacancyCount = data;
    });
  }

  getResumeCount() {
    this.main.getResumeCount().subscribe((data) => {
      this.resumeCount = data;
    });
  }

  getCompanyCount() {
    this.main.getCompanyCount().subscribe((data) => {
      this.companyCount = data;
    });
  }

  onSearch() {
    if (this.search) {
      this.router.navigate(['catalog'], {
        queryParams: { query: this.search },
      });
    }
  }
}
<div class="mil-container">
  <div class="image-container">
    <img
      class="background-image"
      src="./assets/Background_image.jpg"
      alt="img"
    />
    <div class="overlay"></div>
    <div class="image-content" style="padding-top: 50px">
      <h1 style="color: white; margin-bottom: 10px">Найди работу мечты!</h1>
      <div class="search-container">
        <input
          type="text"
          class="search-input"
          id="search"
          name="search"
          placeholder="Найти вакансии..."
          [(ngModel)]="search"
        />
        <button class="search-button" (click)="onSearch()">Найти</button>
      </div>
    </div>
    <div style="width: 602px; position: absolute">
      <div class="numbers-container">
        <div class="numbers">
          <p class="mr">
            {{ vacancyCount }}
          </p>
          <p>Вакансий</p>
        </div>
        <div class="numbers">
          <p class="mr">
            {{ resumeCount }}
          </p>
          <p>Резюме</p>
        </div>
        <div style="display: flex; align-items: center; padding: 20px">
          <p class="mr">
            {{ companyCount }}
          </p>
          <p>Компаний</p>
        </div>
      </div>
    </div>

    <!-- <div class="btn">
      <button class="create-button" routerLink="createVacancy">
        Для компаний
      </button>
    </div> -->
  </div>
</div>
