import { Component, OnInit } from '@angular/core';
import { AboutCompanyService } from './about-company.service';
import { ICompany } from '../interfaces/vacancy';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-about-company',
  templateUrl: './about-company.component.html',
  styleUrls: ['./about-company.component.scss'],
})
export class AboutCompanyComponent implements OnInit {
  company?: ICompany;

  constructor(
    private companyService: AboutCompanyService,
    private activatedRoute: ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.getCompany();
  }

  getCompany() {
    const id = this.activatedRoute.snapshot.paramMap.get('id');
    if (id) {
      this.companyService.getCompany(+id).subscribe({
        next: (data) => {
          this.company = data;
        },
      });
    }
  }
}
<div class="mil-container">
  <div class="card">
    <div class="mil-deco"></div>
    <div class="card-content">
      <div class="card-title">{{ company?.name }}</div>
      <div class="card-subtitle">{{ company?.industry }} $</div>
      <div class="card-location" style="margin-bottom: 10px">
        {{ company?.foundedYear }}
      </div>
      <div class="d-flex">
        <a class="card-button me-3">Откликнуться</a>
        <a class="card-like"><img src="./assets/heart.png" alt="" /></a>
      </div>
    </div>
  </div>
</div>
