import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { IRegister, IResume, IUser } from '../interfaces/appUser';

@Injectable({
  providedIn: 'root',
})
export class RegisterService {
  constructor(private http: HttpClient) {}

  sendData(profileData: IUser, resumeData: IResume) {
    const combined: IRegister = {
      displayName: profileData.displayName,
      firstName: profileData.firstName,
      lastName: profileData.lastName,
      resume: resumeData,
      password: profileData.password,
      phone: profileData.phone,
      email: profileData.email,
    };

    const str = JSON.stringify(combined);
    const pr = JSON.parse(str);

    console.log(`combined: ${str}`);

    if (combined) {
      console.log(`s: ${combined.displayName}`);
      return this.http.post(
        'http://localhost:5045/api/account/register',
        combined
      );
    } else {
      return this.http.post(
        'http://localhost:5045/api/account/register',
        combined
      );
    }
  }
}
