import { Component, OnInit } from '@angular/core';
import { NavigationEnd, Router } from '@angular/router';
import { filter } from 'rxjs';
import { AccountService } from './services/account.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
})
export class AppComponent implements OnInit {
  title = 'HeadHunter';
  isExpired: boolean = true;

  constructor(private router: Router, private accountService: AccountService) {}

  ngOnInit(): void {
    this.router.events
      .pipe(filter((event) => event instanceof NavigationEnd))
      .subscribe(() => {
        this.isTokenExpired();
      });
  }

  reLogin() {
    localStorage.removeItem('TOKEN');
    localStorage.removeItem('DisplayName');
    localStorage.removeItem('BasketId');
    this.isExpired = false;
    this.router.navigate(['login']).then(() => window.location.reload);
  }

  isTokenExpired() {
    this.accountService.isTokenExpired().subscribe({
      next: (res) => {
        // this.isExpired = res;
        console.log(this.isExpired);
      },
    });
  }
}
<div *ngIf="isExpired">
  <div class="overlay">
    <div
      class="modal_op"
      style="
        display: flex;
        align-items: center;
        flex-direction: column;
        background-color: white;
        border-radius: 10px;
        box-shadow: 0 4px 15px rgba(0, 0, 0, 0.2);
        text-align: center;
        width: 500px;
        height: 300px;
        transition: opacity 0.3s ease;
      "
    >
      <h2 style="padding-top: 70px">Срок действия токена истёк</h2>
      <p>
        Пожалуйста, войдите в систему снова, чтобы продолжить использование
        приложения.
      </p>
      <button class="btn" style="margin-top: 20px" (click)="reLogin()">
        Войти снова
      </button>
    </div>
  </div>
</div>

<app-nav-bar></app-nav-bar>

<div id="content_wrapper">
  <router-outlet></router-outlet>
</div>

