import { Component, OnInit } from '@angular/core';
import { ShopParams } from '../interfaces/shopParams';
import { CatalogService } from './catalog.service';
import { IVacancy } from '../interfaces/vacancy';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-catalog',
  templateUrl: './catalog.component.html',
  styleUrls: ['./catalog.component.scss'],
})
export class CatalogComponent implements OnInit {
  vacancies: IVacancy[] = [];
  shopParams = new ShopParams();
  totalCount = 0;
  search: string | null = '';

  constructor(
    private catalogService: CatalogService,
    private activatedRoute: ActivatedRoute
  ) {}

  ngOnInit() {
    if (this.activatedRoute.snapshot.queryParamMap.get('query')) {
      this.activatedRoute.queryParamMap.subscribe((params) => {
        const s = params.get('query');
        if (s !== null) {
          this.getVacancies(s);
        }
      });
    } else {
      this.getVacancies();
    }
  }

  getVacancies(search?: string) {
    if (search) {
      this.shopParams.search = search;
    }
    this.catalogService.getVacancies(this.shopParams).subscribe({
      next: (response) => {
        this.vacancies = response.data;
        this.shopParams.pageNumber = response.pageIndex;
        this.shopParams.pageSize = response.pageSize;
        this.totalCount = response.count;
        console.log(response);
      },
    });
  }

  onPageChanged(event: any) {
    if (this.shopParams.pageNumber !== event) {
      this.shopParams.pageNumber = event;
      this.getVacancies();
    }
  }
}
<div style="position: relative">
  <div>
    <div class="smth" style="display: flex; flex-wrap: wrap">
      <div *ngFor="let item of vacancies">
        <app-card [vacancy]="item"></app-card>
      </div>
    </div>
  </div>
  <app-pager
    *ngIf="totalCount > 6"
    [pageSize]="shopParams.pageSize"
    [totalCount]="totalCount"
    (pageChanged)="onPageChanged($event)"
  ></app-pager>
</div>
