import { Component, OnInit } from '@angular/core';
import { ProfileService } from './profile.service';
import { IResponse, IResume } from '../interfaces/appUser';
import { AccountService } from '../services/account.service';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.scss'],
})
export class ProfileComponent implements OnInit {
  resume?: IResume;
  profileInfo?: IResponse;

  constructor(private accountService: AccountService) {}

  ngOnInit(): void {
    this.getResume();
    this.getProfileInfo();
  }

  getResume() {
    this.accountService.getResume().subscribe({
      next: (data) => {
        this.resume = data;
        console.log(this.resume);
      },
    });
  }

  getProfileInfo() {
    const token = localStorage.getItem('TOKEN');
    if (token) {
      this.accountService.getLoginInfo(token)?.subscribe({
        next: (data) => (this.profileInfo = data),
      });
    }
  }
}
<div class="mil-container">
  <div
    style="
      width: 100px;
      height: 100px;
      border-radius: 10px;
      color: white;
      background-color: blue;
      display: flex;
      justify-content: center;
      align-items: center;
    "
  >
    {{ profileInfo!.displayName[0] }}
  </div>
</div>
