import { Component, OnInit } from '@angular/core';
import { AboutCompanyService } from './about-company.service';
import { ICompany } from '../interfaces/vacancy';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-about-company',
  templateUrl: './about-company.component.html',
  styleUrls: ['./about-company.component.scss'],
})
export class AboutCompanyComponent implements OnInit {
  company?: ICompany;

  constructor(
    private companyService: AboutCompanyService,
    private activatedRoute: ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.getCompany();
  }

  getCompany() {
    const id = this.activatedRoute.snapshot.paramMap.get('id');
    console.log(id);
    if (id) {
      this.companyService.getCompany(+id).subscribe({
        next: (data) => {
          this.company = data;
          console.log(this.company);
        },
      });
    }
  }
}
<div class="mil-container">
  <div class="company-card">
    <div class="card-content">
      <div style="display: flex">
        <img src="./assets/kaspi-logo.png" alt="" class="company-image" />
        <div>
          <div class="company-card__title">
            {{ company?.name }}
          </div>
          <div class="company-card__subtitle">{{ company?.industry }}</div>
        </div>
      </div>
      <div class="card-company mb-1">
        Год основания: {{ company?.foundedYear }}
      </div>
      <div class="card-location" style="margin-bottom: 10px">
        <a class="vac-count" style="cursor: pointer"
          >Вакансии от этой компании: {{ company?.vacancies?.length }}</a
        >
      </div>
    </div>
  </div>
</div>
