import { Component, Input } from '@angular/core';
import { IVacancy } from '../interfaces/vacancy';
import { Router } from '@angular/router';

@Component({
  selector: 'app-card',
  templateUrl: './card.component.html',
  styleUrls: ['./card.component.scss'],
})
export class CardComponent {
  @Input() vacancy?: IVacancy;

  constructor(private router: Router) {}

  redirection(id: number) {
    this.router.navigate(['/catalog/', id]);
  }
}
<div class="card">
  <div class="card-content">
    <div class="card-title">{{ vacancy?.position }}</div>
    <div class="card-subtitle">{{ vacancy?.salary }} $</div>
    <div class="card-company">
      {{ vacancy?.companyName }}
    </div>
    <div class="card-location" style="margin-bottom: 10px">
      {{ vacancy?.location }}
    </div>
    <a [routerLink]="['/catalog', vacancy?.id]" class="card-button"
      >Узнать Больше</a
    >
  </div>
</div>
