    <div class="card-location" style="margin-bottom: 10px">
      {{ vacancy.location }}
    </div>
    <div class="d-flex">
      <a class="card-button me-3" (click)="IsClicked()">Откликнуться</a>
      <a class="card-like" (click)="favoritesService.setFavorites(basket)"
        ><img src="./assets/heart.png" alt=""
      /></a>
    </div>
  </div>
</div>
