import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Basket, IBasket } from '../interfaces/basket';

@Injectable({
  providedIn: 'root',
})
export class FavoritesService {
  url = 'http://localhost:5045/api/basket';
  favorites = new Basket();

  constructor(private http: HttpClient) {}

  getFavorites() {
    return this.http.get<IBasket>(this.url + '?id=' + this.favorites.id);
  }

  setFavorites(basket: IBasket) {
    console.log(`Это баскет: ${basket.id}`);
    // return this.http.post<IBasket>(this.url, basket);
    this.http.post<IBasket>(this.url, basket).subscribe({
      next: (x) => console.log(x),
    });
  }
}
