import { Component, OnInit } from '@angular/core';
import { MainService } from './main.service';
import { IVacancy } from '../interfaces/vacancy';

@Component({
  selector: 'app-main',
  templateUrl: './main.component.html',
  styleUrls: ['./main.component.scss'],
})
export class MainComponent implements OnInit {
  vacancyCount?: number;
  resumeCount?: number;
  companyCount?: number;

  constructor(private main: MainService) {}

  ngOnInit(): void {
    this.getVacancyCount();
    this.getResumeCount();
    this.getCompanyCount();
  }

  getVacancyCount() {
    this.main.getVacancyCount().subscribe((data) => {
      this.vacancyCount = data;
    });
  }

  getResumeCount() {
    this.main.getResumeCount().subscribe((data) => {
      this.resumeCount = data;
    });
  }

  getCompanyCount() {
    this.main.getCompanyCount().subscribe((data) => {
      this.companyCount = data;
    });
  }
}
<!-- <div *ngFor="let item of arr">
  <h4 style="margin-right: 20px">{{ item.position }}</h4>
</div> -->

<div class="container">
  <div class="image-container">
    <img
      class="background-image"
      src="./assets/Background_image.jpg"
      alt="img"
    />
    <div class="overlay"></div>
    <div class="image-content" style="padding-top: 50px">
      <h1 style="color: white; margin-bottom: 10px">Найди работу мечты!</h1>
      <div class="search-container">
        <input
          type="text"
          class="search-input"
          name="search"
          placeholder="Найти вакансии..."
        />
        <button class="search-button">Найти</button>
      </div>
    </div>
    <div class="numbers_container">
      <div class="numbers">
        <p class="mr">
          {{ vacancyCount }}
        </p>
        <p>Вакансий</p>
      </div>
      <div class="numbers">
        <p class="mr">
          {{ resumeCount }}
        </p>
        <p>Резюме</p>
      </div>
      <div style="display: flex; align-items: center; padding: 20px">
        <p class="mr">
          {{ companyCount }}
        </p>
        <p>Компаний</p>
      </div>
    </div>
  </div>
</div>
