import { HttpClient, HttpParams } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { ShopParams } from '../interfaces/shopParams';
import { IPagination } from '../interfaces/pagination';
import { IVacancy } from '../interfaces/vacancy';

@Injectable({
  providedIn: 'root',
})
export class CatalogService {
  constructor(private client: HttpClient) {}

  url = 'http://localhost:5045/api/company/vacancy';

  getVacancies(shopParams: ShopParams) {
    let params = new HttpParams();

    params = params.set('pageIndex', shopParams.pageNumber);
    params = params.set('pageSize', shopParams.pageSize);
    console.log(`shParams: ${shopParams.search}`);

    if (shopParams.search !== null && shopParams.search !== '') {
      params = params.set('search', shopParams.search);
    } else {
      params = params.delete('search');
      console.log(params);
    }

    console.log(`params: ${params}`);

    return this.client.get<IPagination<IVacancy[]>>(this.url, { params });
  }
}
