import { Component } from '@angular/core';
import { NavBarService } from './nav-bar.service';
import { IVacancy } from '../interfaces/vacancy';
import { ActivatedRoute, Router } from '@angular/router';
import { DataService } from '../services/data.service';

@Component({
  selector: 'app-nav-bar',
  templateUrl: './nav-bar.component.html',
  styleUrls: ['./nav-bar.component.scss'],
})
export class NavBarComponent {
  vacancies?: IVacancy[];

  constructor(
    private navbarService: NavBarService,
    private router: Router,
    private dataService: DataService
  ) {}

  getVacancies() {
    this.navbarService.getVacancies().subscribe({
      next: (data) => (this.vacancies = data),
    });
  }

  updateData() {
    if (this.router.url.includes('catalog')) {
      this.getVacancies();
      if (this.vacancies) this.dataService.updateData(this.vacancies);
    }
  }
}
<div class="mil-container">
  <div class="right">
    <img
      routerLink=""
      src="./assets/logo.png"
      alt="logo"
      style="height: 50px; width: auto; cursor: pointer"
    />
    <a routerLink="catalog" style="text-decoration: none" (click)="updateData()"
      >Вакансии</a
    >
    <div class="lang">
      <a>RU</a>
      <a>EN</a>
    </div>
  </div>
  <div class="buttons">
    <button routerLink="login">Войти</button>
    <button routerLink="register">Регистрация</button>
  </div>
</div>
