import { Component } from '@angular/core';
import { AccountService } from '../services/account.service';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss'],
})
export class LoginComponent {
  constructor(private accountService: AccountService, private router: Router) {}

  logInForm: FormGroup = new FormGroup({
    email: new FormControl('', [Validators.required, Validators.email]),
    password: new FormControl('', Validators.required),
  });

  onSubmit() {
    this.accountService.login(this.logInForm.value).subscribe({
      next: (data) => {
        localStorage.setItem('TOKEN', data.token);
        localStorage.setItem('DisplayName', data.displayName);
        localStorage.setItem('BasketId', data.basketId);
        this.router.navigate(['']).then(() => {
          window.location.reload();
        });
      },
    });
  }
}
<div style="display: flex; justify-content: center; align-items: center">
  <div class="lol">
    <form class="form-container" (submit)="onSubmit()" [formGroup]="logInForm">
      <div class="form-group">
        <input
          [class.is-invalid]="
            logInForm.get('email')?.touched && logInForm.get('email')?.invalid
          "
          type="text"
          formControlName="email"
          id="email"
          placeholder="email"
          required
        />
        <div
          *ngIf="logInForm.get('email')?.errors?.['required'] && logInForm.get('email')?.touched"
          style="color: red"
        >
          Пожалуйста укажите email
        </div>
        <div
          *ngIf="logInForm.get('email')?.errors?.['email']"
          style="color: red"
        >
          Неверный email адрес
        </div>
      </div>
      <div class="form-group">
        <input
          [class.is-invalid]="
            logInForm.get('password')?.touched &&
            logInForm.get('password')?.invalid
          "
          type="text"
          formControlName="password"
          id="password"
          placeholder="password"
          required
        />
        <div
          *ngIf="logInForm.get('password')?.errors?.['required'] && logInForm.get('password')?.touched"
          style="color: red"
        >
          Пожалуйста укажите пароль
        </div>
      </div>
      <button class="btn" type="submit" [disabled]="logInForm.invalid">
        Войти
      </button>
    </form>
  </div>
</div>
