<div class="container">
  <div class="image-container">
    <img
      class="background-image"
      src="./assets/Background_image.jpg"
      alt="img"
    />
    <div class="overlay"></div>
    <div class="image-content" style="padding-top: 50px">
      <h1 style="color: white; margin-bottom: 10px">Найди работу мечты!</h1>
      <div class="search-container">
        <input
          type="text"
          class="search-input"
          name="search"
          placeholder="Найти вакансии..."
        />
        <button class="search-button">Найти</button>
      </div>
    </div>
    <div class="numbers">35</div>
  </div>
</div>
