import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { VacancyDetailsService } from './vacancy-details.service';
import { ICompany, IVacancy } from '../interfaces/vacancy';
import { switchMap } from 'rxjs';

@Component({
  selector: 'app-vacancy-details',
  templateUrl: './vacancy-details.component.html',
  styleUrls: ['./vacancy-details.component.scss'],
})
export class VacancyDetailsComponent implements OnInit {
  vacancy?: IVacancy;
  company?: ICompany;

  constructor(
    private activatedRoute: ActivatedRoute,
    private detailsService: VacancyDetailsService
  ) {}

  ngOnInit(): void {
    this.getVacancyAndCompany();
  }

  getVacancyAndCompany() {
    const id = this.activatedRoute.snapshot.paramMap.get('id');
    if (id) {
      this.detailsService.getVacancy(Number(id)).subscribe({
        next: (vac) => {
          this.vacancy = vac;
          this.detailsService.getCompany(vac.companyId).subscribe({
            next: (data) => {
              this.company = data;
            },
          });
        },
      });
    }
    // console.log(this.companyId);
    // if (this.companyId) {
    //   this.detailsService.getCompany(this.companyId).subscribe({
    //     next: (data) => {
    //       this.company = data;
    //       console.log(data);
    //     },
    //   });
    // }
  }
}
<div class="mil-container">
  <div class="d-flex justify-content-center">
    <div class="card">
      <div class="card-content">
        <div class="card-title">{{ vacancy?.position }}</div>
        <div class="card-subtitle">{{ vacancy?.salary }} $</div>
        <div class="card-company">
          {{ vacancy?.companyName }}
        </div>
        <div class="card-location" style="margin-bottom: 10px">
          {{ vacancy?.location }}
        </div>
        <div class="d-flex">
          <a class="card-button me-3">Откликнуться</a>
          <a class="card-like"><img src="./assets/heart.png" alt="" /></a>
        </div>
      </div>
    </div>
    <div class="company-card">
      <div class="card-content">
        <div style="display: flex">
          <img
            src="./assets/kaspi-logo.png"
            alt=""
            style="
              width: 80px;
              height: auto;
              object-fit: cover;
              margin-right: 10px;
            "
          />
          <div>
            <div class="card-subtitle" style="margin-bottom: 0px">
              {{ company?.name }}
            </div>
            <div class="card-title">{{ company?.industry }}</div>
          </div>
        </div>
        <div class="card-company">
          Год основания: {{ company?.foundedYear }}
        </div>
        <div class="card-location" style="margin-bottom: 10px">
          {{ company?.vacancies?.length }}
        </div>
      </div>
    </div>
  </div>
</div>
